"use strict";

var burger = document.querySelector('.burger'),
    menu = document.querySelector('.menu'),
    body = document.querySelector('body');

function burgerMenu() {
  burger.addEventListener('click', function () {
    burger.classList.toggle('burger_active');

    if (burger.classList.contains('burger_active')) {
      menu.classList.add('menu_active');
      body.classList.add('fixed');
      popUp.classList.remove('popUp_activated');
    } else {
      menu.classList.remove('menu_active');
      body.classList.remove('fixed');
    }
  });
  window.addEventListener("resize", function () {
    if (body.clientWidth < 992) {
      burger.classList.remove('burger_active');
      menu.classList.remove('menu_active');
      body.classList.remove('fixed');
    }
  });
}

burgerMenu();
//Trigger 
window.addEventListener("scroll", function () {
  var navbar = document.querySelector('nav');

  if (window.pageYOffset > 700) {
    navbar.classList.add('header__fixed');
  } else {
    navbar.classList.remove('header__fixed');
  }
});

var popUp = document.querySelector('.popUp'),
    wrap = document.querySelector('.wrapper'),
    btnA = document.querySelector('.page__btn'),
    btnB = document.querySelector('.about__btn'),
    popUpClose = document.querySelector('.popUp_close');

function popUpActive() {
  popUp.classList.add('popUp_activated');

  if (popUp.classList.contains('popUp_activated')) {
    wrap.classList.add('popUp__bg');
  } else {
    wrap.classList.remove('popUp__bg');
  }
}

popUpClose.addEventListener('click', function (i) {
  popUp.classList.remove('popUp_activated');
});
btnA.addEventListener('click', function (i) {
  i.preventDefault();
  popUpActive();
});
btnB.addEventListener('click', function (i) {
  i.preventDefault();
  popUpActive();
});
